(function(){$(function(){var i=abp.services.app.user,n=$("#UserCreateModal"),t=n.find("form");t.validate();t.find('button[type="submit"]').click(function(r){if(r.preventDefault(),t.valid()){var u=t.serializeFormToObject();abp.ui.setBusy(n);i.createUser(u).done(function(){n.modal("hide");location.reload(!0)}).always(function(){abp.ui.clearBusy(n)})}});n.on("shown.bs.modal",function(){n.find("input:not([type=hidden]):first").focus()})})})();